    <style id="flocktoryStyleTag" type="text/css">.flockapi-overlay_popup {
            background-color: #00000080;
            background-image: radial-gradient(ellipse closest-corner at 50% 50%, #0000004d 20%, #0009 100%);
            bottom: 0;
            display: none;
            left: 0;
            opacity: 0;
            outline: 0;
            overflow-x: auto;
            overflow-y: auto;
            position: fixed;
            right: 0;
            top: 0;
            z-index: 100000
        }

        .flockapi-overlay_popup .flockapi-spinner {
            display: block
        }

        .flockapi-overlay_popup.flockapi-loaded .flockapi-spinner {
            display: none
        }

        .flockapi-wrapper {
            position: relative
        }

        .flockapi-wrapper_popup {
            display: none;
            margin: 0 auto;
            opacity: 0;
            position: relative;
            transform: scale(.8);
            transition: all .2s linear;
            z-index: 1
        }

        .flockapi-wrapper_popup .flockapi-closebutton {
            right: 20px;
            top: 20px
        }

        .flockapi-wrapper_popup.flockapi-shown {
            transition-property: margin, left, top, transform, opacity
        }

        .flockapi-closebutton {
            background: url("data:image/png;base64,") 50% 50% no-repeat #fff;
            background-size: 10px 10px;
            border-radius: 10px;
            box-shadow: 0 0 0 1px #0003, 0 0 1px 0 #0000001a;
            cursor: pointer;
            height: 20px;
            position: absolute;
            width: 20px;
            z-index: 1
        }

        .flockapi-spinner {
            display: none;
            left: 50%;
            margin: -10px 0 0 -10px;
            position: absolute;
            top: 50%
        }

        .flockapi-spinner, .flockapi-spinner:after {
            box-sizing: border-box;
            position: relative
        }

        .flockapi-spinner {
            color: #fff;
            display: block;
            height: 20px;
            width: 20px
        }

        .flockapi-spinner:after {
            animation: flock-ball-clip-rotate .75s linear infinite;
            background: #0000;
            border: 2px solid;
            border-bottom: 2px solid #0000;
            border-radius: 100%;
            content: "";
            display: inline-block;
            height: 100%;
            width: 100%
        }

        @keyframes flock-ball-clip-rotate {
            0% {
                transform: rotate(0deg)
            }
            to {
                transform: rotate(1turn)
            }
        }

        .flockapi-display_block {
            display: block;
            transition: .2s linear;
            transition-property: left, top, transform, opacity
        }

        .flockapi-show {
            display: block;
            opacity: 1
        }

        .flockapi-show.flockapi-wrapper_popup {
            transform: scale(1)
        }

        .flockapi-wrapper iframe {
            left: 0;
            position: absolute;
            top: 0
        }

        .flockapi-insular-wrapper {
            display: none;
            overflow: hidden;
            visibility: hidden
        }

        .flockapi-insular-wrapper[data-widget-type=embedded] {
            position: static
        }

        .flockapi-insular-wrapper[data-widget-type=fixed] {
            position: fixed
        }

        .flockapi-insular-wrapper[data-widget-type=popup] {
            -webkit-overflow-scrolling: touch;
            background: #000c;
            bottom: 0;
            height: auto;
            left: 0;
            overflow-x: auto;
            overflow-y: auto;
            position: fixed;
            right: 0;
            top: 0;
            width: auto;
            z-index: 1000
        }

        .flockapi-insular-wrapper[data-widget-type=popup] .flockapi-insular-widget {
            margin-left: auto;
            margin-right: auto
        }

        .flockapi-insular-wrapper.flockapi-show {
            display: block;
            visibility: visible
        }

        .flockapi-insular-widget {
            display: block
